const triggerItems = $('Captura Correos').all();
const out = [];

for (const item of triggerItems) {
  for (const [propName, bin] of Object.entries(item.binary || {})) {
    const ext = (bin.fileExtension || '').toLowerCase();
    const mime = (bin.mimeType || '').toLowerCase();

    // Detectar solo PDFs o im√°genes JPG/JPEG
    const isPDF = ext === 'pdf' || mime.includes('application/pdf');
    const isImage =
      ['jpg', 'jpeg'].includes(ext) || mime.includes('image/jpeg');

    if (!isPDF && !isImage) continue; // Saltar archivos que no sean PDF ni JPG/JPEG

    // Obtener buffer del binario
    const buf = bin.data
      ? Buffer.from(bin.data, 'base64')
      : await this.helpers.getBinaryDataBuffer('Captura Correos', 0, propName);

    const fileSize = buf.length;
    const requiresOCR = fileSize > 1000000; // Heur√≠stica por tama√±o > 1MB

    // Preparar binario para el siguiente nodo
    const prepared = await this.helpers.prepareBinaryData(
      buf,
      bin.fileName || `archivo.${ext || (isPDF ? 'pdf' : 'jpg')}`,
      bin.mimeType || (isPDF ? 'application/pdf' : 'image/jpeg')
    );

    // Agregar salida estructurada
    out.push({
      json: {
        messageId: item.json.id,
        fileName: bin.fileName || `sin_nombre.${ext || (isPDF ? 'pdf' : 'jpg')}`,
        fileExtension: ext || (isPDF ? 'pdf' : 'jpg'),
        mimeType: mime || (isPDF ? 'application/pdf' : 'image/jpeg'),
        fileSizeBytes: fileSize,
        fileSizeKB: Math.round(fileSize / 1024),
        fileType: isPDF ? 'pdf' : 'image',
        requiresOCR: requiresOCR,
        detectionMethod: 'byExtensionAndMimeType',
        detectionConfidence: isPDF || isImage ? 'high' : 'low',
      },
      binary: { data: prepared },
    });
  }
}

return out;

