// Recibes el texto completo de la IA
const textoIA = $json.content?.parts?.[0]?.text || $json.text || "";

// Limpiar el texto - mÃºltiples estrategias
let jsonString = textoIA;

// Estrategia 1: Remover los backticks y la palabra 'json'
jsonString = jsonString
  .replace(/```json/g, '')    
  .replace(/```/g, '')        
  .trim();

// Estrategia 2: Si todavÃ­a tiene contenido no JSON, extraer solo el JSON
if (jsonString.includes('{') && jsonString.includes('}')) {
  const inicio = jsonString.indexOf('{');
  const fin = jsonString.lastIndexOf('}') + 1;
  jsonString = jsonString.substring(inicio, fin);
}

// Estrategia 3: Limpiar saltos de lÃ­nea y espacios extras
jsonString = jsonString
  .replace(/\n/g, ' ')        
  .replace(/\s+/g, ' ')       
  .trim();

// Debug
console.log('Texto limpio:', jsonString);

// Parsear a objeto JavaScript
let datosExtraidos;
try {
  datosExtraidos = JSON.parse(jsonString);
  console.log('âœ… JSON parseado correctamente');
} catch (error) {
  try {
    const jsonMatch = textoIA.match(/\{[\s\S]*\}/);
    if (jsonMatch) {
      jsonString = jsonMatch[0];
      datosExtraidos = JSON.parse(jsonString);
      console.log('âœ… JSON parseado con limpieza agresiva');
    } else {
      throw new Error('No se pudo encontrar JSON en el texto');
    }
  } catch (error2) {
    throw new Error(`Error parseando JSON de IA: ${error2.message}. Texto: ${textoIA.substring(0, 200)}...`);
  }
}

// Verificar datos
if (!datosExtraidos || typeof datosExtraidos !== 'object') {
  throw new Error('JSON de IA estÃ¡ vacÃ­o o es invÃ¡lido');
}

console.log(`âœ… ExtraÃ­dos ${Object.keys(datosExtraidos).length} campos`);

return { 
  json: { 
    ...datosExtraidos,   // ðŸ‘ˆ EXPANDE directo el objeto (sin wrapper)
    originalText: textoIA, 
    cleanedText: jsonString,
    messageId: $json.messageId || ""  // â† ESTA LÃNEA ES CRUCIAL
  } 
};

const b = $binary || {};
let count = 0;
for (const [, bin] of Object.entries(b)) {
  const ext = (bin.fileExtension || '').toLowerCase();
  const mt  = (bin.mimeType || '').toLowerCase();
  if (ext === 'pdf' || mt === 'application/pdf') count++;
}
return { json: { messageId: $json.id, pdfCount: count } };

const triggerItems = $('Captura Correos').all();
const out = [];

for (const item of triggerItems) {
  for (const [propName, bin] of Object.entries(item.binary || {})) {
    const ext = (bin.fileExtension || '').toLowerCase();
    const mime = (bin.mimeType || '').toLowerCase();

    if (!(ext === 'pdf' || mime === 'application/pdf')) continue;

    const buf = bin.data 
      ? Buffer.from(bin.data, 'base64')
      : await this.helpers.getBinaryDataBuffer('Gmail Trigger1', 0, propName);

    const fileSize = buf.length; // tamaÃ±o en bytes
    const requiresOCR = fileSize > 1000000; // mayor a 1 MB â†’ escaneado

    const prepared = await this.helpers.prepareBinaryData(
      buf,
      bin.fileName || 'file.pdf',
      bin.mimeType || 'application/pdf'
    );

    out.push({
      json: {
        messageId: item.json.id,
        fileName: bin.fileName || 'sin_nombre.pdf',
        fileExtension: ext,
        mimeType: mime,
        fileSizeBytes: fileSize,
        fileSizeKB: Math.round(fileSize / 1024),
        requiresOCR: requiresOCR,
        detectionMethod: "bySizeOnly",
        detectionConfidence: requiresOCR ? "high" : "medium"
      },
      binary: { data: prepared },
    });
  }
}

return out;

// Limpieza de texto extraÃ­do del PDF

const raw = $json.extractedText || "";
let limpio = raw;

// 1. Eliminar lÃ­neas vacÃ­as o solo con espacios
limpio = limpio
  .split("\n")
  .filter(line => line.trim().length > 0)
  .join("\n");

// 2. Eliminar lÃ­neas con solo sÃ­mbolos repetidos (guiones, puntos, etc.)
limpio = limpio
  .split("\n")
  .filter(line => !/^[\s\.\-_]{5,}$/.test(line))   // solo basura repetida
  .filter(line => !/([.\-_])\1{4,}/.test(line))    // repeticiones largas
  .join("\n");

// 3. Normalizar espacios mÃºltiples
limpio = limpio.replace(/\s{2,}/g, " ");

// 4. (Opcional) Pasar todo a UTF-8 normalizado
limpio = limpio.normalize("NFKC");

return {
  json: {
    textoLimpio: limpio
  }
};

const orden = $json.orden_compra || {};
// AsegÃºrate de recibir el messageId del nodo anterior
const messageId = $json.messageId || "";

return {
  json: {
    tipo: "orden",
    numero: orden.numero || "",
    fecha_orden: orden.fecha_orden || "",
    fecha_entrega: orden.fecha_entrega || "",
    forma_pago: orden.forma_pago || "",
    plazo_pago: orden.plazo_pago || "",
    proveedor_nombre: orden.nombre_empresa || "",
    proveedor_nit: orden.nit_comprador || "",
    direccion: orden.direccion_cliente || "",
    ciudad: orden.ciudad_comprador || "",
    messageId: messageId  // â† AÃ‘ADE ESTO
  }
};

// Recoger todos los correos procesados
const entradas = $input.all();
const out = [];

for (const entrada of entradas) {
  const datos = entrada.json;

  const orden = datos.orden_compra || {};
  const items = Array.isArray(datos.items) ? datos.items : [];

  for (const item of items) {
    out.push({
      json: {
        tipo: "item",
        numero: orden.numero || "",
        codigo: item.codigo || "",
        descripcion: item.descripcion || "",
        presentacion: item.presentacion || "",
        cantidad: item.cantidad || "",
        valor_unitario: item.valor_unitario || "",
        valor_total: item.valor_total || ""
      }
    });
  }
}

return out;

const items = $input.all();
const out = [];

for (const item of items) {
  const pdfText = (item.json.text || "").toUpperCase();

  // 1. Palabras clave (OC y Pedidos)
  const hasKeyword = /(ORDEN DE COMPRA|ORDEN DE COMPRAS No|ODC|ORDEN NÂ°|OC-|PO#|PURCHASE ORDER|PEDIDO|PEDIDO MRP|ORDER)/.test(pdfText);

  // 2. NÃºmero tÃ­pico de orden/pedido
  const orderNumberMatch = pdfText.match(/\b(?:PO|OC|ODC|PEDIDO)[\s\-#:]*\d{3,}\b|\b[A-Z]\d{6,}\b/);
  const hasOrderNumber = !!orderNumberMatch;

  // 3. Proveedor/cliente
  const hasSupplierInfo = /(PROVEEDOR|SUPPLIER|CLIENTE|NIT|RFC)/.test(pdfText);

  // 4. Montos/precios
  const hasPrices = /(?:\d{1,3}([.,]\d{3})+|\d+[.,]\d{2})(\s?(USD|COP|\$))?/.test(pdfText);

  // 5. Indicios de tabla de Ã­tems
  const hasItemsTable = /(ITEM|DESCRIPCION|CANTIDAD|UNITARIO|TOTAL|SUBTOTAL|IVA)/.test(pdfText);

  // Score acumulado
  let score = 0;
  if (hasKeyword) score++;
  if (hasOrderNumber) score++;
  if (hasSupplierInfo) score++;
  if (hasPrices) score++;
  if (hasItemsTable) score++;

  // EvaluaciÃ³n con refuerzo
  let isPO = false;
  let confidence = "low";

  if (score >= 3 && (hasOrderNumber || hasItemsTable)) {
    isPO = true;
    confidence = "high";
  } else if (score === 2 && hasOrderNumber) {
    isPO = true;
    confidence = "medium";
  } else {
    isPO = false;
    confidence = "low";
  }

  out.push({
    json: {
      fileName: item.json.fileName,
      isPurchaseOrder: isPO,
      purchaseOrderConfidence: confidence,
      orderNumber: hasOrderNumber ? orderNumberMatch[0] : null,
      extractedText: item.json.text || ""
    }
  });
}

return out;

