  const row = item.json;

  // Detectar encabezado
  if (row["NOMBRE CLIENTE"]) {
    procesados++;
  }

  // Detectar detalle (ajusta segÃºn tus columnas reales de productos)
  if (row["CODIGO"] || row["DESCRIPCION"]) {
    detalles++;
  }
}

return {
  json: {
    status: "OK",
    ordenesProcesadas: procesados,
    itemsProcesados: detalles,
