    const buf = bin.data 
      ? Buffer.from(bin.data, 'base64')
      : await this.helpers.getBinaryDataBuffer('Gmail Trigger1', 0, propName);

    const fileSize = buf.length; // tamaÃ±o en bytes
    const requiresOCR = fileSize > 1000000; // mayor a 1 MB â†’ escaneado

    const prepared = await this.helpers.prepareBinaryData(
      buf,
      bin.fileName || 'file.pdf',
      bin.mimeType || 'application/pdf'
    );

    out.push({
      json: {
        messageId: item.json.id,
        fileName: bin.fileName || 'sin_nombre.pdf',
        fileExtension: ext,
        mimeType: mime,
        fileSizeBytes: fileSize,
        fileSizeKB: Math.round(fileSize / 1024),
        requiresOCR: requiresOCR,
        detectionMethod: "bySizeOnly",
        detectionConfidence: requiresOCR ? "high" : "medium"
      },
      binary: { data: prepared },
    });
  }
}

return out;
