// Recibes el texto completo de la IA
const textoIA = $json.content?.parts?.[0]?.text || $json.text || "";

// Limpiar el texto - mÃºltiples estrategias
let jsonString = textoIA;

// Estrategia 1: Remover los backticks y la palabra 'json'
jsonString = jsonString
  .replace(/```json/g, '')    
  .replace(/```/g, '')        
  .trim();

// Estrategia 2: Si todavÃ­a tiene contenido no JSON, extraer solo el JSON
if (jsonString.includes('{') && jsonString.includes('}')) {
  const inicio = jsonString.indexOf('{');
  const fin = jsonString.lastIndexOf('}') + 1;
  jsonString = jsonString.substring(inicio, fin);
}

// Estrategia 3: Limpiar saltos de lÃ­nea y espacios extras
jsonString = jsonString
  .replace(/\n/g, ' ')        
  .replace(/\s+/g, ' ')       
  .trim();

// Debug
console.log('Texto limpio:', jsonString);

// Parsear a objeto JavaScript
let datosExtraidos;
try {
  datosExtraidos = JSON.parse(jsonString);
  console.log('âœ… JSON parseado correctamente');
} catch (error) {
  try {
    const jsonMatch = textoIA.match(/\{[\s\S]*\}/);
    if (jsonMatch) {
      jsonString = jsonMatch[0];
      datosExtraidos = JSON.parse(jsonString);
      console.log('âœ… JSON parseado con limpieza agresiva');
    } else {
      throw new Error('No se pudo encontrar JSON en el texto');
    }
  } catch (error2) {
    throw new Error(`Error parseando JSON de IA: ${error2.message}. Texto: ${textoIA.substring(0, 200)}...`);
  }
}

// Verificar datos
if (!datosExtraidos || typeof datosExtraidos !== 'object') {
  throw new Error('JSON de IA estÃ¡ vacÃ­o o es invÃ¡lido');
}

console.log(`âœ… ExtraÃ­dos ${Object.keys(datosExtraidos).length} campos`);

return { 
  json: { 
    ...datosExtraidos,   // ðŸ‘ˆ EXPANDE directo el objeto (sin wrapper)
    originalText: textoIA, 
    cleanedText: jsonString,
    messageId: $json.messageId || ""  // â† ESTA LÃNEA ES CRUCIAL
  } 
};

const b = $binary || {};
let pdfCount = 0;
let imgCount = 0;
let fileType = "";

for (const [, bin] of Object.entries(b)) {
  const ext = (bin.fileExtension || '').toLowerCase();
  const mt  = (bin.mimeType || '').toLowerCase();

  // Detectar PDF
  if (ext === 'pdf' || mt.includes('application/pdf')) {
    pdfCount++;
    fileType = "pdf";
  }

  // Detectar imÃ¡genes comunes (jpg, jpeg, png)
  else if (
    ['jpg', 'jpeg', 'png'].includes(ext) ||
    mt.includes('image/jpeg') ||
    mt.includes('image/png')
  ) {
    imgCount++;
    fileType = "image";
  }
}

// Si hay varios archivos, prioriza PDF sobre imagen
if (pdfCount > 0) fileType = "pdf";
else if (imgCount > 0) fileType = "image";

return {
  json: {
    messageId: $json.id,
    pdfCount,
    imgCount,
    fileType
  }
};

const orden = $json.orden_compra || {};
const messageId = $json.messageId || "";

// Si no tiene nÃºmero, retornar null para que n8n lo omita
if (!orden.numero || orden.numero.trim() === "") {
  console.log(`âŒ Filtrado: Orden sin nÃºmero - MessageId: ${messageId}`);
  return null; // n8n omitirÃ¡ este item automÃ¡ticamente
}

return {
  json: {
    tipo: "orden",
    numero: orden.numero,
    fecha_orden: orden.fecha_orden || "",
    fecha_entrega: orden.fecha_entrega || "",
    forma_pago: orden.forma_pago || "",
    plazo_pago: orden.plazo_pago || "",
    proveedor_nombre: orden.nombre_empresa || "",
    proveedor_nit: orden.nit_comprador || "",
    direccion: orden.direccion_cliente || "",
    ciudad: orden.ciudad_comprador || "",
    messageId: messageId
  }
};

// Recoger todos los correos procesados
const entradas = $input.all();
const out = [];

for (const entrada of entradas) {
  const datos = entrada.json;

  const orden = datos.orden_compra || {};
  const items = Array.isArray(datos.items) ? datos.items : [];

  // ðŸ‘‡ Solo procesar si existe numero y no estÃ¡ vacÃ­o
  if (orden.numero && orden.numero.toString().trim() !== "") {
    for (const item of items) {
      out.push({
        json: {
          tipo: "item",
          numero: orden.numero,
          codigo: item.codigo_producto || "",
          descripcion: item.descripcion || "",
          presentacion: item.presentacion || "",
          cantidad: item.cantidad || "",
          valor_unitario: item.precio_unitario || "",
          valor_total: item.subtotal || ""
        }
      });
    }
  }
}

return out;

parameters.functionCode:
const triggerItems = $('Captura Correos').all();
const out = [];

for (const item of triggerItems) {
  for (const [propName, bin] of Object.entries(item.binary || {})) {
    const ext = (bin.fileExtension || '').toLowerCase();
    const mime = (bin.mimeType || '').toLowerCase();

    // Detectar PDFs o imÃ¡genes vÃ¡lidas
    const isPDF = ext === 'pdf' || mime.includes('application/pdf');
    const isImage = ['jpg', 'jpeg', 'png'].includes(ext) || mime.startsWith('image/');

    if (!isPDF && !isImage) continue; // Saltar archivos que no sean PDF ni imagen

    // Obtener buffer del binario
    const buf = bin.data
      ? Buffer.from(bin.data, 'base64')
      : await this.helpers.getBinaryDataBuffer('Captura Correos', 0, propName);

    const fileSize = buf.length;
    const requiresOCR = fileSize > 1000000; // HeurÃ­stica por tamaÃ±o > 1MB

    // Preparar binario para el siguiente nodo
    const prepared = await this.helpers.prepareBinaryData(
      buf,
      bin.fileName || `archivo.${ext || (isPDF ? 'pdf' : 'jpg')}`,
      bin.mimeType || (isPDF ? 'application/pdf' : 'image/jpeg')
    );

    // Agregar salida estructurada
    out.push({
      json: {
        messageId: item.json.id,
        fileName: bin.fileName || `sin_nombre.${ext || (isPDF ? 'pdf' : 'jpg')}`,
        fileExtension: ext || (isPDF ? 'pdf' : 'jpg'),
        mimeType: mime || (isPDF ? 'application/pdf' : 'image/jpeg'),
        fileSizeBytes: fileSize,
        fileSizeKB: Math.round(fileSize / 1024),
        fileType: isPDF ? 'pdf' : 'image',
        requiresOCR: requiresOCR,
        detectionMethod: 'byExtensionAndMimeType',
        detectionConfidence: isPDF || isImage ? 'high' : 'low',
      },
      binary: { data: prepared },
    });
  }
}

return out;

