const items = $input.all();
let procesados = 0;
let detalles = 0;

for (const item of items) {
  const row = item.json;

  // Detectar encabezado
  if (row["NOMBRE CLIENTE"]) {
    procesados++;
  }

  // Detectar detalle (ajusta segÃºn tus columnas reales de productos)
  if (row["CODIGO"] || row["DESCRIPCION"]) {
    detalles++;
  }
}

return {
  json: {
    status: "OK",
    ordenesProcesadas: procesados,
    itemsProcesados: detalles,
    timestamp: new Date().toISOString()
  }
};

